import React, { createContext, useReducer } from 'react';

const CartContext = createContext();

const initialState = {
  items: {}, // This will hold items with their id as key and quantity as value
};

function cartReducer(state, action) {
  switch (action.type) {
    case 'ADD_ITEM':
      return {
        ...state,
        items: {
          ...state.items,
          [action.payload.id]: (state.items[action.payload.id] || 0) + 1,
        },
      };
    case 'REMOVE_ITEM':
      if (!state.items[action.payload.id]) return state;
      const updatedQuantity = state.items[action.payload.id] - 1;
      return {
        ...state,
        items: {
          ...state.items,
          [action.payload.id]: updatedQuantity > 0 ? updatedQuantity : undefined,
        },
      };
    default:
      return state;
  }
}

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, initialState);

  return (
    <CartContext.Provider value={{ state, dispatch }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
